import React, { useState } from 'react';

function CodeEditor() {
  const [html, setHtml] = useState('');
  const [css, setCss] = useState('');
  const [js, setJs] = useState('');

  const srcDoc = `
    <html>
      <body>${html}</body>
      <style>${css}</style>
      <script>${js}</script>
    </html>
  `;

  return (
    <div style={{ display: 'flex' }}>
      <div style={{ flex: 0.3 }}>
        <h2>Placeholder for left-side content</h2>
      </div>

      <div style={{ flex: 1 }}>
        <section>
          <textarea
            value={html}
            onChange={(e) => setHtml(e.target.value)}
            placeholder="HTML"
          />
          <textarea
            value={css}
            onChange={(e) => setCss(e.target.value)}
            placeholder="CSS"
          />
          <textarea
            value={js}
            onChange={(e) => setJs(e.target.value)}
            placeholder="JavaScript"
          />
        </section>

        <section>
          <iframe
            srcDoc={srcDoc}
            title="output"
            sandbox="allow-scripts"
            frameBorder="0"
            width="100%"
            height="100%"
          />
        </section>
      </div>
    </div>
  );
}

export default CodeEditor;
